import React, { useState }from 'react'
import classes from './home.module.css'

import Chero from '../../components/section/c-hero/c-hero';
import Projects from '../../components/section/projects/projects';
import Contact from '../../components/section/contact/contact';
import Footer from '../../components/footer/footer';
import DarkMode from '../../hoc/darkmode/darkmode';
import Header from '../../components/header/header';
import Modal from '../../components/section/contact/modal/modal';

const Home = () => {
  const [modal, setModal] = useState(false);

  const toggle = () => {
    setModal(!modal);
  }
  return (
    <>
        <Header />
        <Chero />
        <Projects />
        <Contact />
        <Footer />
        <DarkMode style={classes.icon}/>
        <button onClick={toggle}>CLICK MODAL</button>
        <Modal show={modal} close={toggle}/>

    </>
  )
}

export default Home;
