import React, { useState } from 'react'
import classes from './modal.module.css'


const Modal = ({ show, close }) => {
    const [modalPoint , setModalPoint] = useState(0);
    const [contactValue, setContactValue] = useState([]);

    const incModalPoint = () =>{
        if(modalPoint < 2){
            setModalPoint(modalPoint + 1);
        }
        if(modalPoint == 2) {
            console.log('test');
        }
    }
    const desModalPoint = () => {
        if(modalPoint > 0) {

            setModalPoint(modalPoint - 1);
            const nvCVU = [...contactValue];
            nvCVU[modalPoint - 1] = {
                id: modalPoint, 
                value: null};
            setContactValue(nvCVU);

        }
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        incModalPoint();
        const nvCV = [...contactValue];
        nvCV[modalPoint] = {
            id: modalPoint, 
            value: event.target[0].value};
        setContactValue(nvCV);
        event.target[0].value = '';
    }
    console.log(contactValue)
    const handleChange = (event) => {
        event.preventDefault();
    }

    let labelform = '';
    let back = '';
    let filled1 = '';
    let filled2 = '';
    switch(modalPoint){
        default :
            labelform = 'Votre nom';
            break;
        case 1:
            labelform = 'Votre mail';
            back = <button className="submit" onClick={desModalPoint}>Back</button>;
            filled1 = classes.filled;
            break;
        case 2:
            labelform = 'Votre message';
            back = <button className="submit" onClick={desModalPoint}>Back</button>;
            filled1 = classes.filled;
            filled2 = classes.filled;
            break;
    }

    return (
      <>
       {
       show ?

       <div
          className={classes.con} 
        >
          <div className={classes.modal}  >
            <header className={classes.header}>
              <div>
                {back}
              </div>
              <div className={classes.progress}>
                <div className={classes.formProgressStep + ' ' + classes.filled}></div>
                <div className={classes.formProgressStep + ' ' + filled1}></div>
                <div className={classes.formProgressStep + ' ' + filled2}></div>
              </div>
              <button className="modal-close" onClick={close} >
                Cancel
              </button>

            </header>

            <main className={classes.main}>
                <form onSubmit={(e) => handleSubmit(e)}>
                    <label>
                        {labelform}
                        <input onChange={(e) => handleChange(e)} />
                    </label>
                    <input type="submit" value="Next" />
                </form>
            </main>
          </div>
        </div>
        : null
       }
      </>
    );
  };

  export default Modal;
