import React, { useState } from 'react'
import classes from './modal.module.css'


const Modal = ({ show, close }) => {
    const [modalPoint , setModalPoint] = useState(0);

    const incModalPoint = () =>{
        if(modalPoint < 3){
            setModalPoint(modalPoint + 1);
        }
        console.log(modalPoint);
    }
    const desModalPoint = () => {
        if(modalPoint > 0) {
            setModalPoint(modalPoint - 1);
        }
        console.log(modalPoint);
    }
    switch(modalPoint){
        case 0:
            console.log('test 0 ')
            break;
        case 1:
            console.log('test 1')
            break;
        case 2:
            console.log('test 2')
            break;
        case 3:
            console.log('test 3')
            break;
    }
    return (
      <>
       {
       show ?

       <div
          className={classes.con} 
        >
          <div className="modal" >
            <header className="modal_header">
              <div>
              <button className="submit" onClick={desModalPoint}>back</button>
              </div>
              <div className={classes.title}>
                Modal adavac
              </div>
              <button className="modal-close" onClick={close} >
                Cancel
              </button>

            </header>
            <main className="modal_content">
            This is Modal Content
            </main>
            <footer className="modal_footer">
            <div>
              <button className="submit" onClick={incModalPoint}>next</button>
            </div>

            </footer>
          </div>
        </div>
        : null
       }
      </>
    );
  };

  export default Modal;
