import React, { useState } from 'react'
import classes from './modal.module.css'


const Modal = ({ show, close }) => {
    const [modalPoint , setModalPoint] = useState(0);
    const [contactValue, setContactValue] = useState({});

    const handleSubmit = (event) => {
        event.preventDefault();
        incModalPoint();
        setContactValue(modalPoint, event.target.value);
        console.log(contactValue);
    }

    const handleChange = (event) => {
        event.preventDefault();
        console.log(event.target.value)
    }

    const incModalPoint = () =>{
        if(modalPoint < 2){
            setModalPoint(modalPoint + 1);
        }
    }
    const desModalPoint = () => {
        if(modalPoint > 0) {
            setModalPoint(modalPoint - 1);
        }
    }
    let labelform = 'Votre nom';
    switch(modalPoint){
        case 0:
            labelform = 'Votre nom';
            break;
        case 1:
            labelform = 'Votre mail';
            break;
        case 2:
            labelform = 'Votre message';
            break;
    }
    return (
      <>
       {
       show ?

       <div
          className={classes.con} 
        >
          <div className={classes.modal}  >
            <header className={classes.header}>
              <div>
              <button className="submit" onClick={desModalPoint}>back</button>
              </div>
              <div className={classes.progress}>
                <div className={classes.formProgressStep}></div>
                <div className={classes.formProgressStep}></div>
                <div className={classes.formProgressStep}></div>
              </div>
              <button className="modal-close" onClick={close} >
                Cancel
              </button>

            </header>

            <main className={classes.main}>
                <form onSubmit={(e) => handleSubmit(e)}>
                    <label>
                        {labelform}
                        <input onChange={(e) => handleChange(e)} />
                    </label>
                    <input type="submit" value="Envoyer" />
                </form>
            </main>
            <footer className={classes.footer}>
                <button className="submit" onClick={incModalPoint}>next</button>
            </footer>
          </div>
        </div>
        : null
       }
      </>
    );
  };

  export default Modal;
