import { ReactComponent as WorkIcon } from "./svg/work.svg"
import { ReactComponent as SchoolIcon } from "./svg/school.svg"

import timelineElements from "./timelineElements"
import classes from './timeline.module.css'
import {
  VerticalTimeline,
  VerticalTimelineElement,
} from "react-vertical-timeline-component"

import "react-vertical-timeline-component/style.min.css"
import { useContext } from "react"
import { ThemeContext } from "../../../hoc/darkmode/themeContext"

function TimeLine() {
  let workIconStyles = { background: "#70DAC2" }
  let schoolIconStyles = { background: "#f9c74f" }
  const styleTimelineElements = {
    textAlign: 'left',
    backgroundColor: "#F5F5F7"
   }
  const darkTimeLineElements = {
    textAlign: 'left',
    backgroundColor: '#1d1d1f'
  }

  const {theme} = useContext(ThemeContext)
  return (

    <div className={classes.timecon}>
      <VerticalTimeline lineColor={ '#314663' }>
        {timelineElements.map(element => {
          let isWorkIcon = element.icon === "work"
          let showButton =
            element.buttonText !== undefined &&
            element.buttonText !== null &&
            element.buttonText !== ""

          return (
            <VerticalTimelineElement
                contentStyle={theme ? styleTimelineElements : darkTimeLineElements}
                contentArrowStyle={theme ? styleTimelineElements : darkTimeLineElements}
                key={element.key}
                date={element.date}
                dateClassName="date"
                iconStyle={isWorkIcon ? workIconStyles : schoolIconStyles}
                icon={isWorkIcon ? <WorkIcon /> : <SchoolIcon />}
            >
              <h3 className="vertical-timeline-element-title">
                {element.title}
              </h3>
              <h5 className="vertical-timeline-element-subtitle">
                {element.location}
              </h5>
              <p id="description">{element.description}</p>
              {showButton && (
                <a
                  className={classes.btn}
                  href="/"
                >
                  {element.buttonText}
                </a>
              )}
            </VerticalTimelineElement>
          )
        })}
      </VerticalTimeline>
    </div>
  )
}

export default TimeLine
